window.onload = function() {
  const loader = document.getElementById('loader');
  const heading = document.querySelector('.beyondJS');
  const menimg = document.querySelector('.menzoominJs');
  const line = document.querySelector('.line');
  const img = document.querySelector('.loader-inner img');

  // Hide the loader
  gsap.to(loader, { opacity: 0, duration: 0.3, delay: 0.5, onComplete: removeLoader });

  // Show the heading
  gsap.to(heading, { opacity: 1, duration: 0.5, delay: 1 });

  // Show the Image
  gsap.to(menimg, { opacity: 1.2, duration: 0.6, delay: 1 });

  // Animate the line
  const timeline = gsap.timeline({
    onUpdate: updateProgress,
    onComplete: completeLoading
  });

  timeline.fromTo(line, { scaleX: 0 }, { scaleX: 1, duration: 1, ease: 'power2.out' });

  // Update the progress of the line animation
  function updateProgress() {
    const progress = gsap.getProperty(line, 'scaleX') * 100;
    img.setAttribute('alt', 'Preloader ' + Math.round(progress) + '%');
  }

  // Remove the loader and show the complete content when loading is complete
  function completeLoading() {
    loader.style.display = 'none';
    // You can add code here to reveal the main content of your website
    // For example, you can fade in the main content
    gsap.to('.main-content', { opacity: 1, duration: 1 });
  }

  function removeLoader() {
    loader.style.display = 'none';
  }
};

